var colors = require('colors');
module.exports = function(conf) {
	return require('./console')({
		filters : {
			log : filterLog,
			trace : colors.magenta,
			debug : colors.cyan,
			data : [colors.blue, colors.bold],
			info : colors.green,
			warn : colors.yellow,
			error : [ colors.red, colors.bold],
			fatal : colors.red
		}
	}, conf);
};

function filterLog(str, data) {
	switch(data.title.toLowerCase()){
		case "debug": return str.cyan;
		case "data": return str.blue;
		case "fatal": return str.red.italic.inverse;
	}
	return str;
}